<script>
export default {
  mounted() {
    gsap.registerPlugin(MotionPathPlugin)

    this.animate()
  },
  methods: {
    animate() {
      gsap.set('#mask', { y: '+=500' })
      gsap.set('#sun', { scale: 0.5, opacity: 0 })
      gsap.to('#sun', {
        delay: 0.5,
        scale: 1.0,
        opacity: 100,
        duration: 2.0,
        ease: 'bounce.out',
        motionPath: {
          path: '#path',
          align: '#path',
          autoRotate: true,
          alignOrigin: [0.5, 0.5],
        },
      })
      gsap.to('#mask', {
        y: '-=500',
        duration: 2.5,
        delay: 1.0,
        ease: 'power2.inOut',
      })
      gsap.to('#leaf', {
        // y: '+=1',
        // x: '+=1',
        // ease: 'bounce.inOut',
        delay: 2.5,
        yoyo: true,
        // delay: 2.5,
        repeat: -1,
        duration: 2.0,
        rotation: '-=3',
        transformOrigin: 'bottom bottom',
        ease: Linear.easeNone,
      })
      gsap.to('#stem', {
        // x: '+=1',
        // ease: Sine.easeInOut,
        delay: 2.5,
        yoyo: true,
        repeat: -1,
        duration: 2.0,
        rotation: '+=1',
        transformOrigin: 'bottom bottom',
        ease: Linear.easeNone,
      })
      gsap.to('#sun', 0.7, {
        y: '-=7',
        ease: Sine.easeInOut,
        yoyo: true,
        delay: 2.5,
        repeat: -1,
      })
    },
  },
}
</script>

