  methods: {
    contactScroll() {
      let topics = document.querySelector('.topics').cloneNode(true)
      document.getElementById('news').appendChild(topics)

      gsap.to('.topics', 30, {
        x: -topics.offsetWidth,
        repeat: -1,
        ease: Linear.easeNone,
      })
    },
    gearRotate() {
      gsap.to('#gearOne', 3.5, {
        rotation: '360_cw',
        repeat: -1,
        ease: Sine.easeInOut,
      })
      gsap.to('#smallGear', 3.5, {
        delay: 1,
        rotation: '360_cw',
        repeat: -1,
        ease: Sine.easeInOut,
        transformOrigin: '50%',
      })
      gsap.to('#bigGear', 3.5, {
        delay: 1,
        rotation: '360_ccw',
        repeat: -1,
        ease: Sine.easeInOut,
        transformOrigin: '50%',
      })
    },
  },
}
</script>

